(
    product_id integer,
    qty integer,
    PRIMARY KEY (product_id)
);

CREATE TABLE IF NOT EXISTS public.order_header
(
    order_id bigserial,
    user_id integer,
    order_date timestamp with time zone,
    PRIMARY KEY (order_id)
);

