DO $$
BEGIN
IF EXISTS ( SELECT * FROM cart WHERE product_id = 1) THEN 
UPDATE cart SET qty = qty + 1 WHERE product_id = 1;
ELSE 
INSERT INTO cart(product_id, qty) VALUES(1, 3);
END IF;
END $$
SELECT * FROM cart

SELECT 
cart.product_id,
p_m.name,
p_m.price,
cart.qty
FROM cart AS cart
JOIN products_menu AS p_m
ON cart.product_id = p_m.product_id;


